mapper.select:
select eo_pro_id, eo_id, createtime, ep_name, eo_cost, eo_number, ep_file_name, eo_status, rn from ( select t.eo_pro_id, t.eo_id, t.createtime, t.ep_name, t.eo_cost, t.eo_number, t.ep_file_name, t.eo_status, rownum rn from ( select orde.eo_pro_id, orde.eo_id, to_char(orde.eo_create_time ,'yyyy-mm-dd HH24:mi:ss') createtime, product.ep_name, orde.eo_cost, orde.eo_number, product.ep_file_name, orde.eo_status from easybuyorder orde inner join easybuyproduct product on orde.eo_pro_id = product.ep_id where 1 =1
and orde.eo_user_id = #{userId}
and orde.eo_id = #{eoId}
and orde.eo_status = #{status}
order by orde.eo_id DESC )t where rownum < #{max} ) where rn > #{min}

  select count(1)
 from
easybuyorder orde
inner join
easybuyproduct product
on  orde.eo_pro_id = product.ep_id
where 1 =1 

 and   orde.eo_user_id = #{userId}


 and   orde.eo_id = #{eoId}


 and   orde.eo_status = #{status}

mapper.update:
update EASYBUYORDER
set  EO_STATUS = #{eoStatus,jdbcType=VARCHAR}
 where EO_ID = #{eoId,jdbcType=DECIMAL}
