mapper.select:
select EP_ID, EP_NAME, EP_DESCRIPTION, EP_PRICE, EP_STOCK, EPC_ID, EPC_CHILD_ID, EP_FILE_NAME, EP_REBATE, EP_SALECOUNT, rn from (select EP_ID, EP_NAME, EP_DESCRIPTION, EP_PRICE, EP_STOCK, EPC_ID, EPC_CHILD_ID, EP_FILE_NAME, EP_REBATE, EP_SALECOUNT, rownum rn from ( select EP_ID, EP_NAME, EP_DESCRIPTION, cast(EP_PRICE*EP_REBATE as decimal(10, 2) ) AS EP_PRICE, EP_STOCK, EPC_ID, EPC_CHILD_ID, EP_FILE_NAME, EP_REBATE, EP_SALECOUNT from EASYBUYPRODUCT where 1=1
and EPC_CHILD_ID = #{epcChildId,jdbcType = DECIMAL}
and EPC_ID = #{epcId,jdbcType = DECIMAL}
and EP_REBATE <= #{epRebate,jdbcType = DECIMAL}
) where rownum < #{maxNum,jdbcType=DECIMAL} ) where rn > #{minNum,jdbcType=DECIMAL}

select 
     count(1)
 from 
     EASYBUYPRODUCT
  where 1=1

     and EPC_CHILD_ID = #{epcChildId,jdbcType = DECIMAL}


     and EPC_ID = #{epcId,jdbcType = DECIMAL}


      and EP_REBATE <= #{epRebate,jdbcType = DECIMAL}

mapper.update:
update EASYBUYPRODUCT
set   EP_STOCK = #{epStock,jdbcType=DECIMAL}
 where EP_ID = #{epId,jdbcType=DECIMAL}
